import { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import AuthContext from '../../context/AuthContext';
import './Register.css';

const Register = () => {
  const [name, setName] = useState('');
  const [dob, setDob] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Mock register function for demonstration
    console.log('Register attempt:', { name, dob, email, password });
    
    // In your actual app, you would call: 
    const result = await register(name, dob, email, password);
     if (result.success) { navigate('/dashboard'); }
  };

  return (
    <div className="auth-container">
      <div className="register-card">
        {/* Header */}
        <div className="register-header">
          <div className="sign-up-badge">SIGN UP</div>
          <div className="user-avatar">
          </div>
        </div>

        {/* Error Message */}
        {error && <p className="error">{error}</p>}

        {/* Form */}
        <div className="register-form">
          <div className="form-group">
            <div className="input-wrapper">
              <input
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}
                placeholder="name"
                required
                className="form-input"
              />
            </div>
          </div>

          <div className="form-group">
            <div className="input-wrapper">
              <input
                type="date"
                value={dob}
                onChange={(e) => setDob(e.target.value)}
                required
                className="form-input date-input"
              />
            </div>
          </div>

          <div className="form-group">
            <div className="input-wrapper">
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="email"
                required
                className="form-input"
              />
            </div>
          </div>

          <div className="form-group">
            <div className="input-wrapper">
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="password"
                required
                className="form-input"
              />
            </div>
          </div>

          <div className="form-options">
            <label className="terms-checkbox">
              <input type="checkbox" required />
              <span>I agree to the Terms & Conditions</span>
            </label>
          </div>

          <button type="button" onClick={handleSubmit} className="register-button">REGISTER</button>
          
          <div className="login-link">
            Already have an account? <a href="#" onClick={() => console.log('Navigate to login')}>Sign In</a>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Register;
