import { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import AuthContext from '../../context/AuthContext';
import { User, Mail, Lock } from 'lucide-react';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const { login } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const result = await login(email, password);
      if (result.success) {
        navigate('/dashboard');
      } else {
        setError(result.message || 'An error occurred');
      }
    } catch (err) {
      setError('Network error. Please try again.');
      console.error('API Error:', err);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-teal-400 to-teal-600 flex items-center justify-center p-4">
      <div className="bg-slate-800 rounded-lg shadow-2xl p-8 w-full max-w-md">
        {/* Header */}
        <div className="text-center mb-8">
          <div className="bg-teal-400 text-slate-800 px-6 py-2 rounded font-semibold text-sm inline-block mb-6">
            SIGN IN
          </div>
          
          {/* User Avatar */}
          <div className="w-20 h-20 bg-slate-600 rounded-full flex items-center justify-center mx-auto mb-6">
            <User className="w-10 h-10 text-slate-400" />
          </div>
        </div>

        {/* Error Message */}
        {error && (
          <div className="bg-red-500/10 border border-red-500/20 text-red-400 px-4 py-2 rounded mb-4 text-sm">
            {error}
          </div>
        )}

        {/* Form */}
        <form onSubmit={handleSubmit} className="space-y-4">
          {/* Email Input */}
          <div className="relative">
            <Mail className="w-5 h-5 text-slate-400 absolute left-3 top-1/2 transform -translate-y-1/2" />
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="email"
              required
              className="w-full bg-slate-700 text-white placeholder-slate-400 pl-10 pr-4 py-3 rounded focus:outline-none focus:ring-2 focus:ring-teal-400 focus:bg-slate-600 transition-colors"
            />
          </div>

          {/* Password Input */}
          <div className="relative">
            <Lock className="w-5 h-5 text-slate-400 absolute left-3 top-1/2 transform -translate-y-1/2" />
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="password"
              required
              className="w-full bg-slate-700 text-white placeholder-slate-400 pl-10 pr-4 py-3 rounded focus:outline-none focus:ring-2 focus:ring-teal-400 focus:bg-slate-600 transition-colors"
            />
          </div>

          {/* Remember Me and Forgot Password */}
          <div className="flex items-center justify-between text-sm pt-2">
            <label className="flex items-center text-slate-400">
              <input type="checkbox" className="mr-2 accent-teal-400" />
              Remember me
            </label>
            <a href="#" className="text-teal-400 hover:text-teal-300 transition-colors">
              Forgot password?
            </a>
          </div>

          {/* Login Button */}
          <button
            type="submit"
            className="w-full bg-teal-400 hover:bg-teal-500 text-slate-800 font-semibold py-3 rounded transition-colors transform hover:scale-[1.02] active:scale-[0.98] mt-6"
          >
            LOGIN
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
