import { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import AuthContext from '../../context/AuthContext';
import './Register.css'; // We'll share the same CSS as Login

const Register = () => {
  const [name, setName] = useState('');
  const [username, setUsername] = useState('');
  const [dob, setDob] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const { register } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const result = await register(name, dob, username, password);
    if (result.success) {
      navigate('/dashboard');
    } else {
      setError(result.message);
    }
  };

  return (
    <div className="login-container"> {/* Reusing login styles */}
      <h2 className="login-title">REGISTER</h2>
      
      <form onSubmit={handleSubmit} className="login-form">
        <div className="form-group">
          <input
            type="text"
            placeholder="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            className="login-input"
            required
          />
        </div>

        <div className="form-group">
          <input
            type="text"
            placeholder="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className="login-input"
            required
          />
        </div>
        
        <div className="form-group">
          <input
            type="date"
            value={dob}
            onChange={(e) => setDob(e.target.value)}
            className="login-input"
            required
          />
        </div>

        <div className="form-group">
          <input
            type="password"
            placeholder="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="login-input"
            required
          />
        </div>

        {error && <p className="login-error">{error}</p>}

        <div className="login-options">
          <span className="advisers-text">Already have an account?</span>
        </div>

        <button type="submit" className="login-button">REGISTER</button>
      </form>
    </div>
  );
};

export default Register;
